custom_css:
.page-heading .back-history:before {top: 0;}
.header-wrapper.header-double-menu .container-wrapper, .sidebar-widget .widget-title, .widget_categories ul li, 
.product-share, .filter-wrap, .widget_product_categories.sidebar-widget ul li a, .after-shop-loop, .quick-view-popup .quick-view-excerpts .excerpt-title, #reviews #comments > h2,
.comment-reply-title, .block-title, table th, table td, .cart_totals th, .related-posts .title {
    border-bottom: 1px solid rgba(0,0,0,0.1);
}
.product-share, .filter-wrap, .after-shop-loop, .quick-view-popup .quick-view-excerpts .excerpt-title, #reviews #comments > h2, .comment-reply-title, table, .block-title,
.woocommerce table.wishlist_table thead th, .woocommerce table.wishlist_table tbody td, .share-post, .author-info, .related-posts .title{
    border-top: 1px solid rgba(0,0,0,0.1);
}

h1.vc_custom_heading {
  text-transform: capitalize;
}
h2.vc_custom_heading {
  text-transform: capitalize;
}
h3.vc_custom_heading {
  text-transform: capitalize;
}
h4.vc_custom_heading {
  text-transform: capitalize;
}
h5.vc_custom_heading {
  text-transform: capitalize;
}
h6.vc_custom_heading {
  text-transform: capitalize;
}
.post-heading h2{text-transform:capitalize;font-size:28px;}

.button, .btn, input[type="submit"], .woocommerce table.wishlist_table td.product-add-to-cart a, sidebar-widget .tagcloud a {
    border: 1px solid #faf8f2;
    background-color: #faf8f2;
}
/**--- Shop page styles--- **/
.widget_product_categories .widget-title{
    background-color: #403b29!important;
}
.view-switcher .switch-grid.switcher-active a, .pagination-cubic ul li span.current, .pagination-cubic ul li a:hover, .carousel-area .owl-next, .carousel-area .owl-prev, .posts-nav-btn .button {
    background-color: #96907e !important;
}
.pagination-cubic ul li span.current, .pagination-cubic ul li a:hover {
    border-color: #96907e!important;
}
div.fancy-select div.trigger, div.fancy-select div.trigger.open, .view-switcher .switch-list a, .view-switcher .switch-grid a, 
.pagination-cubic ul li a, .pagination-cubic ul li span, .cart_totals {
    background-color: #faf8f2;
}
.order-review{background-color:transparent;}
.tagcloud a:hover{background-color: #827957 !important;border-color: #827957 !important;}
.content-product .product-title a{    
    font-size: 22px;
    text-transform: none;
    color: #403b29;
    font-family: "Averia Serif Libre";
    font-weight: 300;
}
.content-product .product-title a:hover{opacity:0.6;}

/** --- Single Product --- **/
.single-product-large .product-information-inner {
    border: 20px solid #e7e4db;
}
.zoom-images-button, .open-video-popup {
    background-color: #e0dbca;
}
.quantity.buttons_added span {
    background-color: transparent;
    border: 1px solid rgba(0, 0, 0, 0.1);
}

input[type="text"], input[type="number"], input[type="email"], textarea.form-control, input[type="search"], input[type="password"], input[type="tel"], textarea, .select2-container, .select2-drop-active {
    border: 1px solid rgba(0, 0, 0, 0.1);
}
.product-information .yith-wcwl-add-to-wishlist a:before, .product-info-wrapper .yith-wcwl-add-to-wishlist a:before {
    background-color: #f3f0e7 !important;
}
.single_add_to_cart_button {
    background-color: #e93939 !important;
    border-color: #e93939 !important;
}
.single_add_to_cart_button:hover {
    border-color: #ff6666 !important;
    background-color: #ff6666 !important;
}
.carousel-area .owl-next:before,
.carousel-area .owl-prev:before{color:#cbcbcb;}
/** --- Footer --- **/
.footer {
    padding: 4.7em 0 0;
    background-color: #292825;
}

.footer .et-follow-buttons a{
    padding: 0 11px;
}
.footer .et-follow-buttons a:first-child{
    padding-left: 0;
}
.footer .et-follow-buttons a:last-child{
    padding-right: 0;
}
.footer-bottom {
    padding: 2rem 0;
    background-color: #292825;
}
.footer.text-color-light a {
    color: #a5a093;
}
.footer-links-bakery a{font-size:18px;margin-right:24px;margin-left:24px; font-family: "Averia Serif Libre";}
.footer-links-bakery a:first-child{margin-left:0;}
.footer-links-bakery a:last-child{margin-right:0;}
.content-page, .page-content {
    padding-bottom: 0em;
}
.order-review table th{border-bottom:1px solid rgba(0,0,0,0.1);}
.cart-bag .badge-number, .et-wishlist-widget .wishlist-count{background-color: #e93939 !important;}
.quick-view-popup .quick-view-excerpts .excerpt-title:before{color:#252525;}
body #intercom-container .intercom-launcher-frame{    bottom: 38px!important;
    right: 115px!important;}
    .empty-cart-block{background-image:url('http://8theme.com/demo/xstore/bakery/wp-content/themes/xstore-bakery/images/empty-cart-dark.png');}
    .login input[type="submit"]{background-color:#403b29;margin-right:20px;color:#fff;}
    .login input[type="submit"]:hover{opacity:0.7;}
