custom_css:
#etheme-popup {border:none;}
#etheme-popup label{color:#fff;}
#etheme-popup .checkbox-label {
    left: 60px;
    bottom: 25px;
}
.mfp-close {
    right: -15px;
    top: 50px;
}
#etheme-popup .mfp-close {
 background: transparent; 
}
#etheme-popup .mfp-close:hover {
 background: transparent !important; 
}
#etheme-popup .mfp-close:before {
    color: #fff !important;
}
#etheme-popup .mfp-close:hover:before {
    color: #d64444 !important;
}
.back-top{bottom:45px;}

/*Temporary fix for My Account Page*/
.woocommerce-MyAccount-navigation {
max-width: 260px;
width: 260px;
display: inline-block;
margin-right: 30px;
}
.woocommerce-MyAccount-navigation ul {
padding-left: 0;
}
.woocommerce-MyAccount-navigation li {
list-style: none;
border-top: 1px solid #e6e6e6;
margin-bottom: 0;
}
.woocommerce-MyAccount-navigation li a {
padding: 1.29em 0;
text-transform: uppercase;
display: inline-block;
font-size: 14px;
font-weight: 400;
line-height: 1.48;
}
.woocommerce-MyAccount-content {
display: inline-block;
vertical-align: top;
width: 847px;
}
.col-md-9 .woocommerce-MyAccount-navigation {
width: 200px;
}
.col-md-9 .woocommerce-MyAccount-content {
width: 614px;
}
@media only screen and (max-width: 1200px) {
.woocommerce-MyAccount-navigation {
width: 220px;
}
.woocommerce-MyAccount-content {
width: 687px;
}
.col-md-9 .woocommerce-MyAccount-navigation {
width: 190px;
}
.col-md-9 .woocommerce-MyAccount-content {
width: 474px;
}
}
@media only screen and (max-width: 992px) {
.woocommerce-MyAccount-navigation {
width: 100% !important;
max-width: 100% !important;
margin-right: 0;
}
.woocommerce-MyAccount-content {
width: 100% !important;
}
}
