custom_css:
h3.vc_custom_heading {
    letter-spacing: 7px;
    
}
.header-wrapper.header-double-menu .container-wrapper {
    border-bottom: none;
}
.page-heading .title {
    letter-spacing: 7px;
}
.btn {
    border: 1px solid #ddd8ca;
    background-color: #ddd8ca;
}
select{border:1px solid #dbd7ca;}
textarea.form-control, textarea{background-color:transparent;border: 1px solid #dbd7ca;}
.sidebar select{min-width:100%;}
.btn.bordered {
    border: 2px solid #c8c5bb;
    color: #262626;
}
input[type="text"], input[type="number"], input[type="email"], input[type="search"], input[type="password"], input[type="tel"], textarea {
    border: 1px solid #d9d9d9;
}

/** --- Single Product --- **/
.single-product .page-heading {
    display: none;
}

.single-product .woocommerce-tabs {
    background-color: #fff;
    padding: 10vh 1vh 5vh;
}

/** --- Footer --- **/
.footer {
    padding: 4em 0 0;
    background-color: #292825;
}

.footer .et-follow-buttons a{
    padding: 0 11px;
}
.footer .et-follow-buttons a:first-child{
    padding-left: 0;
}
.footer .et-follow-buttons a:last-child{
    padding-right: 0;
}
.footer-bottom {
    padding: 2rem 0;
    background-color: #292825;
}
.footer.text-color-light a {
    color: #a5a093;
}
.footer-links-drinks a{font-size:18px;margin-right:24px;margin-left:24px;}
.footer-links-drinks a:first-child{margin-left:0;}
.footer-links-drinks a:last-child{margin-right:0;}
.content-page, .page-content {
    padding-bottom: 0em;
}
/* Mailchimp */
.mc4wp-form .drinks >p{position:relative;}
.mc4wp-form .drinks input[type="email"]{margin-bottom:10px; display:inline-block;height:50px; width:55% !important;margin-right:20px; background-color: rgba(255,255,255,0.7);color:#c8c5bb; border:none !important; }
.mc4wp-form .drinks input[type="submit"]{height:50px;background-color: rgba(196, 186, 154, 1);
    border: 2px solid rgba(196, 186, 154,1);
    width: 25%;
    font-family: 'Roboto Condensed';
    display: inline-block;
    font-size:15px;
    color:#fff;
    transition:all 0.2s linear !important;
    vertical-align: middle;}
    .mc4wp-form .drinks input[type="submit"]:hover{background-color: #d7d0ba;border: 2px solid #d7d0ba;}
.mc4wp-form .drinks ::-webkit-input-placeholder {
  color: #c8c5bb;
}
.mc4wp-form .drinks :-moz-placeholder {
  /* Firefox 18- */
  color: #c8c5bb;
}
.mc4wp-form .drinks ::-moz-placeholder {
  /* Firefox 19+ */
  color: #c8c5bb;
}
.mc4wp-form .drinks :-ms-input-placeholder {
  color: #c8c5bb;
}
.top-bar{font-family: 'Roboto Condensed';color:#787878;}
.top-bar a{color:#787878;}
.top-bar a:hover{color:#000;}
.button, .btn, input[type="submit"]{font-family: 'Roboto Condensed';}

/* Products */
.content-product .product-title a{font-size:24px;font-family: Georgia;line-height:1;margin-bottom:10px; text-transform: capitalize;font-weight:400;letter-spacing:-0.25px;}
.content-product .products-page-cats{color:#a79e84;}
.content-product .price{color:#a79e84;font-size:18px;letter-spacing:1px; font-family: 'Roboto Condensed';}
.content-product .price ins span{color:#bd2424;}
.content-product .products-page-cats a{ font-family: 'Roboto Condensed';font-weight:bold;font-size:14px;color:#a79e84;letter-spacing:1px;}
.woocommerce-review-link{color: #a48e52;}
.single-product-center .price{font-family: 'Roboto Condensed';font-size:22px;color:#a79e84;letter-spacing:1px;}
.single-product-center .price ins span{color:#bd2424}
@media (min-width: 767px){
    #intercom-container .intercom-launcher-frame, #intercom-container .intercom-launcher-badge-frame{
    display: inline-block;
    margin-right: 90px !important;
    margin-bottom: 19px !important;
}
#cookie-notice {
	left: 1vw;
    right: auto;
}

#intercom-container .intercom-app-launcher-enabled .intercom-chat {
    bottom: 115px!important;
}
}
#intercom-container .intercom-app-launcher-enabled .intercom-notifications, body #intercom-container .intercom-app-launcher-enabled .intercom-messenger-frame{
    bottom: 110px!important;
}



.et-demo-versions .greenbtn {
    border:none;
    font-family: 'Lato', sans-serif;
}
.et-demo-versions,
.et-versions-head h2{
    font-family: "Lato", sans-serif;
}
