custom_css:
/*--- Header ---*/
.header-color-dark .menu-wrapper .menu > li > a:hover:after{background-color: rgba(255, 255, 255, 0.4);}
.fixed-header {
    background-color: #349fe2;
}
.fixed-header .item-level-0 > a, 
.fixed-header .menu-item-language > a, 
.fixed-header .navbar-header .et-wishlist-widget > a, 
.fixed-header .navbar-header .search-btn, .fixed-header .navbar-header .cart-summ, 
.fixed-header .menu-wrapper .menu > li.current-menu-item > a, 
.fixed-header .shopping-container.ico-design-2.ico-bg-yes .cart-bag:before {
    color: #fff !important;
}
.fixed-header .cart-bag, 
.fixed-header .cart-bag:before, 
.fixed-header .cart-summ:hover .cart-bag, 
.fixed-header .cart-summ:hover .cart-bag:before  {
    border: 2px solid #fff;
}

.fixed-header .cart-bag .badge-number, 
.fixed-header .cart-summ:hover .cart-bag .badge-number {
    color: #fff;
}
.fixed-header .navbar-header .et-wishlist-widget a:hover, 
.fixed-header .navbar-header .search-btn:hover, 
.fixed-header .navbar-header .cart-summ:hover {
    opacity: 0.6;
}
.fixed-header.header-color-dark .navbar-toggle .icon-bar{
	background-color:#fff;
}
.fixed-header{box-shadow:none;}
.cart_list.product_list_widget .buttons a:first-child, 
.widget_shopping_cart .btn-view-cart,
.checkout-button,
.et-wishlist-widget .wishlist-dropdown .buttons .btn-view-wishlist, 
.btn, input[type="submit"],
.widget_shopping_cart .btn-view-cart, .woocommerce .wishlist_table a.add_to_cart.button.alt {
    border-radius: 30px;
}

h1.vc_custom_heading,
h2.vc_custom_heading,
h3.vc_custom_heading,
h3.vc_custom_heading,
h4.vc_custom_heading,
h5.vc_custom_heading,
h6.vc_custom_heading {
  text-transform: capitalize;
}
.topbar-widget.etheme_widget_socials .et-follow-buttons {
    background-color: #1e87ca !important;
}
.top-bar.topbar-color-white {
    border-bottom-color: #2193db;
}
.topbar-widget.etheme_widget_socials .et-follow-buttons a:last-child,
.languages-area > div {
    border-right: none;
}
.topbar-widget.etheme_widget_socials .et-follow-buttons a,
.top-links > div {
    border-left: none;
}
.cart-bag .badge-number, 
.shopping-container.ico-design-2 .cart-bag .badge-number,
.fixed-header .et-wishlist-widget .wishlist-count {
    background-color:#fff !important; 
    color:#1e1e1e !important;
}
.fixed-header{
    border-top-width:2px;
}
@media only screen and (max-width:992px){
    .fixed-header .shopping-container.ico-design-3 .cart-bag:before, 
    .fixed-header .shopping-container.ico-design-2 .cart-bag:before{
        color:#fff !important;
    }
    .fixed-header .cart-bag .badge-number, 
    .fixed-header .shopping-container.ico-design-2 .cart-bag .badge-number{
        background-color: #fff !important;
        color: #1e1e1e !important;
    }
}
.header-xstore .menu-wrapper{width:70%;}
.cart-popup-container, .et-wishlist-widget .wishlist-dropdown{top:85px;}
.et-wishlist-widget:hover .wishlist-dropdown, 
.wishlist-widget-show .et-wishlist-widget .wishlist-dropdown {top:61px;}
.fixed-header .et-wishlist-widget:hover .wishlist-dropdown, 
.fixed-header .wishlist-widget-show .et-wishlist-widget .wishlist-dropdown {top:55px;}
.fixed-header .shopping-container:hover .cart-popup-container,
.fixed-header .shopping-container.cart-show .cart-popup-container{top:61px;}
.shopping-container.cart-show .cart-popup-container,
.shopping-container:hover .cart-popup-container{top:68px;}
.mc4wp-form .et-mailchimp input[type="submit"]{right:5px;}
.testimonials-slider blockquote{font-family:'Open Sans', sans-serif;}
.widget_search input[type="text"]{border-radius:15px;width:95%;}
.widget_search button{right:-3px;}
.et-tabs-wrapper .tabs-nav li.et-opened:before{
    border-left-color:transparent !important;
    border-right-color:transparent !important;
}
@media only screen and (min-width: 1200px) {
    .vc_row[data-vc-full-width='true'] .owl-carousel .owl-next {
        right: -70px !important;
    }
    .vc_row[data-vc-full-width='true'] .owl-carousel .owl-prev {
        left: -70px !important;
    }
}
@media only screen and (max-width: 480px){ 
.page-heading, .et-header-overlap .page-heading, 
.et-header-overlap .page-heading.bc-size-small, .page-heading.bc-size-small {
    padding-top: 2em!important;
    padding-bottom: 2em;
}
}

.et-demo-versions .greenbtn {border-radius:0;}

/*--- Contact form ---*/
.hosting-contact-form span input:focus, .hosting-contact-form span textarea:focus {
    border-color: #1793e1;
}
.hosting-contact-form span input{
    width: 100%;
    border: 2px solid #e6e6e6;
    border-radius: 15px;
    background: transparent;
    height: 47px;
}
.hosting-contact-form span textarea{
    border: 2px solid #e6e6e6;
    border-radius: 15px;
}
.hosting-contact-form textarea{
    background-color: #fff;
}
.hosting-contact-form  [type="submit"]{
    background-color: #349fe2;
    color: #fff;
    border-color: #349fe2;
}
.mc4wp-form .et-mailchimp input[type="email"] {
    background-color: transparent;
    border: 2px solid rgba(250, 250, 250, 0.3);
    }


/*--- Shop Page ---*/
.product-view-booking .content-product {
    border-color: #f2f8fa;
}
.price ins .amount, 
.product-view-booking .price .amount{
    font-family: Montserrat;
    color: #1793e1;
}
.wishlist_table thead {
    background: transparent;
}
.onsale,
.content-product .sale-wrapper.with-percentage .sale-value {
    height: 2em;
    line-height: 2em;
    border-radius: 0;
    top: 0;
    left:0;
}
    

/*--- Single Product Page ---*/    
.single-product .content-page {
    margin-top: 4.5vw;
}
.single-product-booking .product-side-information-inner > div {
    background-color: #f2f8fa;
}
.single_add_to_cart_button, 
.view-color-dark .button, 
.before-checkout-form .button{
    border-radius: 30px;
    min-width: auto;
}
.single-product-booking .product-side-information-inner .quantity.buttons_added span:hover,
.single-product-booking .product-side-information-inner .quantity.buttons_added span:hover{
    background-color: #112955;
}
/* --- Buttons ---*/
/* --- Light blue ---*/
.single_add_to_cart_button, 
.view-color-dark .button, 
.before-checkout-form .button,
.shopping-container .btn-view-cart:hover,
.before-checkout-form .button:hover,
.et-wishlist-widget .wishlist-dropdown .buttons .btn-view-wishlist:hover,
.woocommerce .wishlist_table a.add_to_cart.button.alt:hover
{
    background-color: #349fe2 !important;
    border-color: #349fe2 !important;
}
.single_add_to_cart_button:hover,
.form-row.place-order input[type="submit"]:hover, 
.before-checkout-form .button,
.view-color-dark .button:hover,
.widget_shopping_cart .btn-view-cart,
.btn.active:hover,
.et-wishlist-widget .wishlist-dropdown .buttons .btn-view-wishlist,
.woocommerce .wishlist_table a.add_to_cart.button.alt,
.hosting-contact-form  [type="submit"]:hover {
    border-color: #112955 !important;
}
.btn.active:hover {
    opacity: 1 !important;
}
.single_add_to_cart_button:hover, 
.form-row.place-order input[type="submit"]:hover, 
.before-checkout-form .button,
.view-color-dark .button:hover,
.widget_shopping_cart .btn-view-cart,
.btn.active:hover,
.et-wishlist-widget .wishlist-dropdown .buttons .btn-view-wishlist,
.woocommerce .wishlist_table a.add_to_cart.button.alt,
.hosting-contact-form  [type="submit"]:hover {
    background-color: #112955 !important;
}
/*--- Footer ---*/
.footer.text-color-dark .widget-title {
	color: #112955;
}
.footer .et-follow-buttons a, .mc4wp-form .et-mailchimp input[type="email"] {
    border-radius: 30px;
}
.footer.text-color-dark a {
	color: #349fe2;
}
.footer p {
	font-size: 14px;
	margin-bottom: 15px;
}
